import React, { useState } from 'react';
import { usePaystackPayment } from 'react-paystack';

const PaystackPayment = ({ 
  email, 
  amount, 
  currency, 
  eventName, 
  customerName,
  registrationId,
  onSuccess, 
  onClose,
  publicKey,
  disabled = false
}) => {
  const [isProcessing, setIsProcessing] = useState(false);

  // Generate unique reference
  const reference = `evt_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  const config = {
    reference,
    email,
    amount: Math.round(amount * 100), // Convert to kobo/cents
    currency: currency || 'NGN',
    publicKey: publicKey || process.env.REACT_APP_PAYSTACK_PUBLIC_KEY,
    metadata: {
      eventName,
      customerName,
      registrationId,
      custom_fields: [
        {
          display_name: "Event Name",
          variable_name: "event_name",
          value: eventName
        },
        {
          display_name: "Customer Name",
          variable_name: "customer_name",
          value: customerName
        }
      ]
    },
    channels: ['card', 'bank', 'ussd', 'qr', 'mobile_money', 'bank_transfer']
  };

  const initializePayment = usePaystackPayment(config);

  const handlePayment = async () => {
    if (disabled || isProcessing) return;
    
    try {
      setIsProcessing(true);

      // Optional: Initialize transaction on your backend first
      const initResponse = await fetch('http://localhost:5000/api/paystack/initialize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          email,
          amount,
          currency,
          eventName,
          customerName,
          registrationId
        })
      });

      const initData = await initResponse.json();
      
      if (!initData.status) {
        throw new Error(initData.message || 'Failed to initialize payment');
      }

      // Launch Paystack checkout
      initializePayment(
        // Success callback
        async (reference) => {
          try {
            console.log('Payment successful:', reference);
            
            // Verify payment on backend
            const verifyResponse = await fetch(`http://localhost:5000/api/paystack/verify/${reference.reference}`, {
              method: 'GET'
            });
            
            const verifyData = await verifyResponse.json();
            
            if (verifyData.status && verifyData.data.status === 'success') {
              onSuccess({
                reference: reference.reference,
                status: 'success',
                message: 'Payment completed successfully!',
                paymentData: verifyData.data
              });
            } else {
              throw new Error('Payment verification failed');
            }
          } catch (error) {
            console.error('Payment verification error:', error);
            onSuccess({
              reference: reference.reference,
              status: 'pending',
              message: 'Payment submitted. Verification in progress...',
              error: error.message
            });
          }
        },
        // Close callback
        () => {
          console.log('Payment dialog closed');
          onClose();
        }
      );
    } catch (error) {
      console.error('Payment initialization error:', error);
      alert(`Payment Error: ${error.message}`);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="space-y-4">
      {/* Payment Method Info */}
      <div className="bg-green-50 p-4 rounded-lg border border-green-200">
        <div className="flex items-center gap-2 mb-2">
          <div className="w-8 h-8 bg-green-600 rounded flex items-center justify-center">
            <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
          </div>
          <h3 className="font-semibold text-green-800">Paystack Payment</h3>
        </div>
        <p className="text-sm text-green-700">
          Secure payment with cards, bank transfer, USSD, and mobile money
        </p>
        <div className="mt-2 flex flex-wrap gap-1">
          <span className="px-2 py-1 bg-white rounded text-xs text-green-700 border border-green-300">Card</span>
          <span className="px-2 py-1 bg-white rounded text-xs text-green-700 border border-green-300">Bank Transfer</span>
          <span className="px-2 py-1 bg-white rounded text-xs text-green-700 border border-green-300">USSD</span>
          <span className="px-2 py-1 bg-white rounded text-xs text-green-700 border border-green-300">Mobile Money</span>
        </div>
      </div>

      {/* Payment Summary */}
      <div className="bg-gray-50 p-4 rounded-lg">
        <h4 className="font-medium text-gray-800 mb-2">Payment Summary</h4>
        <div className="space-y-1 text-sm">
          <div className="flex justify-between">
            <span className="text-gray-600">Event:</span>
            <span className="text-gray-800">{eventName}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">Amount:</span>
            <span className="text-gray-800 font-semibold">{currency} {amount.toLocaleString()}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">Email:</span>
            <span className="text-gray-800">{email}</span>
          </div>
        </div>
      </div>

      {/* Payment Button */}
      <button
        onClick={handlePayment}
        disabled={disabled || isProcessing || !email || !amount}
        className={`w-full py-4 rounded-xl font-semibold text-lg transition-all duration-300 flex items-center justify-center gap-2 ${
          disabled || isProcessing || !email || !amount
            ? 'bg-gray-400 cursor-not-allowed text-gray-200'
            : 'bg-green-600 hover:bg-green-700 text-white hover:scale-105 shadow-lg hover:shadow-xl'
        }`}
      >
        {isProcessing ? (
          <>
            <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
            Processing...
          </>
        ) : (
          <>
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
            </svg>
            Pay with Paystack
          </>
        )}
      </button>

      {/* Security Notice */}
      <p className="text-xs text-gray-500 text-center">
        ðŸ”’ Your payment is secured by Paystack's industry-standard encryption
      </p>
    </div>
  );
};

export default PaystackPayment;
